function VerCuota(a,b,x) {
if (x==1) {
  document.getElementById(a).style.display = 'none';
  document.getElementById(b).style.display = "block";}

}
function NoVerCuota(a,b,x) {
if (x==1) {
  document.getElementById(a).style.display = 'none';
  document.getElementById(b).style.display = "block";}

}